import React, { Component } from "react";
import DeleteBtn from "../components/DeleteBtn";
import Jumbotron from "../components/Jumbotron";
import API from "../utils/API";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../components/Grid";
import { List, ListItem } from "../components/List";
import { Input, TextArea, FormBtnSearch, FormBtnSave } from "../components/Form";

class Books extends Component {
  state = {
    savedMovies: [],
    foundMovie: false,
    searchInput: "",
    title: "",
    year: "",
    actors: ""
  };

  componentDidMount() {
    this.loadBooks();
  }

  loadBooks = () => {
    API.getBooks()
      .then(res =>
        this.setState({ books: res.data, title: "", author: "", synopsis: "" })
      )
      .catch(err => console.log(err));
  };

  deleteBook = id => {
    API.deleteBook(id)
      .then(res => this.loadBooks())
      .catch(err => console.log(err));
  };

  handleInputChange = event => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };

  handleFormSubmit = event => {
    event.preventDefault();
      API.search(this.state.searchInput)
        .then(res => {
          console.log(res);
          this.setState({title: res.data.Title, actors: res.data.Actors, year: res.data.Year })
        })
        .catch(err => console.log(err));

  };

  render() {
    return (
      <Container fluid>
        <Row>
          <Col size="md-6">
            <Jumbotron>
              <h1>Book Searcher</h1>
            </Jumbotron>
            <form>
              <Input
                value={this.state.searchInput}
                onChange={this.handleInputChange}
                name="searchInput"
                placeholder="Search for a book"
              />
              <FormBtnSearch
                onClick={this.handleFormSubmit}
              >
                Search
              </FormBtnSearch>
            </form>
          </Col>

          <Col size="md-6 sm-12">
            <Jumbotron>
              <h1>Books On My List</h1>
            </Jumbotron>
            {this.state.savedMovies.length ? (
              <List>
                {this.state.savedMovies.map(movie => (
                  <ListItem key={movie._id}>
                    <Link to={"/books/" + movie._id}>
                      <strong>
                        {movie.title}
                      </strong>
                    </Link>
                    <DeleteBtn onClick={() => this.deleteBook(movie._id)} />
                  </ListItem>
                ))}
              </List>
            ) : (
              <h3>No Results to Display</h3>
            )}
          </Col>
        </Row>
        <Row>
          <Col size="md-12">
              <h1 name="title">{this.state.title}</h1>
              <p name="year">{this.state.year}</p>
              <p name="actors">{this.state.actors}</p>

          </Col>
        </Row>
      </Container>
    );
  }
}

export default Books;
